                    <div className="modal-buttons">
                        <button className="btnModal" id="sumarCarrito" onClick={this.agregarCarrito}>Agregar</button>
                    </div>
                </div>


            </section>

        </div>

        )

    }
    render(){
        return(
            <React.Fragment>

                {this.modal()}

            </React.Fragment>
        )
    }

}
