import React from 'react'
import PageLoading from '../component/pageLoading'
import Lista from '../component/lista'
import api from '../app'

class Productos extends React.Component{

    constructor(props) {

        super(props)

        this.state = {
            loading: true,
            error: null,
            data: undefined
        }
    }
    componentDidMount(){
     this.fetchData()
    }

    fetchData = async () => {
        this.setState({ loading: true, error: null })

        try {
            const data = await api.tienda.listProductos()
            const data2=data.body;
            this.setState({ loading: false, data: data2 })

        } catch (error) {
            this.setState({ loading: false, error: error })

        }

    }


    render(){
        // if (this.state.loading === true) {
        //     return <PageLoading/>
        // }else if(this.state.error){
        //     return (<h1>datas</h1>);
        //     // return `Eror: ${this.state.error.message}`;
        //   }    
        return(
            <React.Fragment>

                    <main className="main">
                        <article className="container-producto">
    {/* <h1>hola peerro</h1>         */}

                            <Lista Productos={this.state.data} />

                        </article>
                    </main>

            </React.Fragment>
        )
    }
}

export default Productos;
